import classes from "./Answer.module.css";
import { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";

function Answer(props) {
  const navigate = useNavigate();
  const [up, setup] = useState(props.ans.upvotes);
  const [down, setdown] = useState(props.ans.downvotes);
  const loggedIn = useSelector((state) => state.auth.loggedIn);
  console.log(props.ans._id);
  const like = () => {
    if (!loggedIn) {
      navigate("/auth");
      return;
    }
    const token = localStorage.getItem("token");
    fetch("http://localhost:8080/vote", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + token,
      },
      body: JSON.stringify({
        subtopicId: props.subtopicId,
        answerId: props.ans._id,
        questionId: props.questionId,
        upvote: true,
      }),
    })
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        console.log(data);
        if (data.upvotes) {
          console.log("upppp");
          setup(data.upvotes);
        }
        if (data.downvotes) {
          console.log("dnnnnn");
          setdown(data.downvotes);
        }
      });
  };
  const dislike = () => {
    if (!loggedIn) {
      navigate("/auth");
      return;
    }
    const token = localStorage.getItem("token");
    fetch("http://localhost:8080/vote", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + token,
      },
      body: JSON.stringify({
        subtopicId: props.subtopicId,
        answerId: props.ans._id,
        questionId: props.questionId,
        upvote: false,
      }),
    })
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        console.log(data);
        data.upvotes && setup(data.upvotes);
        data.downvotes && setdown(data.downvotes);
      });
  };
  return (
    <p className={classes.answer}>
      {props.ans.answer}
      <span className={classes.dislike} onClick={dislike}>
        <ion-icon name="thumbs-down"></ion-icon>
        {down}
      </span>
      <span className={classes.like} onClick={like}>
        {up}
        <ion-icon name="thumbs-up"></ion-icon>
      </span>
    </p>
  );
}

export default Answer;
