import React, { Fragment, useEffect, useState } from "react";
import classes from "./Topic.module.css";
import { useParams, useSearchParams } from "react-router-dom";
import { TopicData } from "../Data/TopicData";
import { TopicQA } from "../Data/TopicQA";
// import { Plus } from "react-bootstrap-icons";
// import Overl from "../Components/Overl";
// import { HandThumbsUpFill, HandThumbsDownFill } from "react-bootstrap-icons";

function Topic() {
  const [filter, setfilter] = useSearchParams();
  const [subtopicData, setsubtopicData] = useState({});
  const params = [];
  for (let entry of filter.entries()) {
    params.push(entry);
  }
  useEffect(() => {
    fetch("http://localhost:8080/subtopicData/React/React Routing")
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        console.log(data);
        setsubtopicData(data.subtopicData);
      });
  }, []);
  // let code =
  //   TopicData[Object.fromEntries([...filter])["tech"]][
  //     Object.fromEntries([...filter])["topic"]
  //   ][0];
  // let description =
  //   TopicData[Object.fromEntries([...filter])["tech"]][
  //     Object.fromEntries([...filter])["topic"]
  //   ][1];
  // console.log(TopicQA);
  // console.log(TopicQA[params.title][params.topic]);

  let answers = subtopicData.questions.map((qa) => (
    //  console.log(Object.keys(qa)[0],Object.values(qa)[0]);
    <p>
      <p className={classes.question}>
        {qa.question}
        {/* <Overl className={classes.overl} message={"Add answer"} /> */}
      </p>
      {questions.answers.map((ans) => (
        <p className={classes.answer}>
          {ans}
          <a href="#" className={classes.like}>
            <ion-icon name="thumbs-up"></ion-icon>
          </a>
          <a href="#" className={classes.dislike}>
            <ion-icon name="thumbs-down"></ion-icon>
          </a>
        </p>
      ))}
    </p>
  ));

  return (
    <div>
      <div className={classes.d}>
        <div className={classes.topic}>{subtopicData.name}</div>
        <p className={classes.code}>{subtopicData.codeSample}</p>
        <p className={classes.desc}>{subtopicData.description}</p>
      </div>
      <div className={classes.d}>
        <div className={classes.topic}>Discussions</div>

        <hr />

        {/* <div> {answers}</div> */}
      </div>
    </div>
  );
}

export default Topic;
