import React, { useState } from "react";
import classes from "./MyAnswers.module.css";

function MyAnswers() {
  const [qas, setqas] = useState([]);
  //fetch answers and send as props
  const token = localStorage.getItem("token");
  fetch("http://localhost:8080/allAnswers", {
    method: "GET",
    headers: {
      Authorization: "Bearer " + token,
    },
  })
    .then((res) => {
      return res.json();
    })
    .then((data) => {
      setqas(data.qalist);
    });
  const qa = qalist.map((qa) => {
    return (
      <div>
        <p className={classes.ques}>{qa.question}</p>
        <p className={classes.ans}>{qa.answer}</p>
      </div>
    );
  });
  return (
    <div>
      <h2 className={classes.qas}>My Answers</h2>
      <hr></hr>
      <p>{qa}</p>
    </div>
  );
}

export default MyAnswers;
