import classes from "./Answer.module.css";
import { useState } from "react";

function Answer(props) {
  const [up, setup] = useState(props.ans.upvotes);
  const [down, setdown] = useState(props.ans.downvotes);
  console.log(props.ans._id);
  const like = () => {
    const token = localStorage.getItem("token");
    console.log(props.ans._id);
    fetch("http://localhost:8080/vote", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + token,
      },
      body: JSON.stringify({
        subtopicId: props.subtopicId,
        answerId: props.ans._id,
        questionId: props.questionId,
        upvote: true,
      }),
    })
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        console.log(data);
      });
  };
  const dislike = () => {
    const token = localStorage.getItem("token");
    fetch("http://localhost:8080/vote", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + token,
      },
      body: JSON.stringify({
        subtopicId: props.subtopicId,
        answerId: props.ans._id,
        questionId: props.questionId,
        upvote: false,
      }),
    })
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        console.log(data);
        setup;
      });
  };
  return (
    <p className={classes.answer}>
      {props.ans.answer}
      <span className={classes.dislike} onClick={dislike}>
        <ion-icon name="thumbs-down"></ion-icon>
        {props.ans.downvotes}
      </span>
      <span className={classes.like} onClick={like}>
        {props.ans.upvotes}
        <ion-icon name="thumbs-up"></ion-icon>
      </span>
    </p>
  );
}

export default Answer;
