import React, { useEffect, useState } from "react";
import classes from "./MyAnswers.module.css";

function MyAnswers() {
  const [qas, setqas] = useState([]);
  const [qa, setqa] = useState([]);
  const [up, setup] = useState(props.ans.upvotes);
  const [down, setdown] = useState(props.ans.downvotes);
  const like = () => {
    if (!loggedIn) {
      navigate("/auth");
      return;
    }
    const token = localStorage.getItem("token");
    // fetch("http://localhost:8080/vote", {
    //   method: "POST",
    //   headers: {
    //     "Content-Type": "application/json",
    //     Authorization: "Bearer " + token,
    //   },
    //   body: JSON.stringify({
    //     subtopicId: props.subtopicId,
    //     answerId: props.ans._id,
    //     questionId: props.questionId,
    //     upvote: true,
    //   }),
    // })
    //   .then((res) => {
    //     return res.json();
    //   })
    //   .then((data) => {
    //     if (data.message === "vote done") {
    //       setup(data.upvotes);
    //       setdown(data.downvotes);
    //     }
    //   });
  };
  const dislike = () => {
    if (!loggedIn) {
      navigate("/auth");
      return;
    }
    const token = localStorage.getItem("token");
    // fetch("http://localhost:8080/vote", {
    //   method: "POST",
    //   headers: {
    //     "Content-Type": "application/json",
    //     Authorization: "Bearer " + token,
    //   },
    //   body: JSON.stringify({
    //     subtopicId: props.subtopicId,
    //     answerId: props.ans._id,
    //     questionId: props.questionId,
    //     upvote: false,
    //   }),
    // })
    //   .then((res) => {
    //     return res.json();
    //   })
    //   .then((data) => {
    //     if (data.message === "vote done") {
    //       setup(data.upvotes);
    //       setdown(data.downvotes);
    //     }
    //   });
  };
  //fetch answers and send as props
  useEffect(() => {
    const token = localStorage.getItem("token");
    fetch("http://localhost:8080/allAnswers", {
      method: "GET",
      cache: "no-store",
      headers: {
        Authorization: "Bearer " + token,
      },
    })
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setqas(data.answers);
      });
  }, []);
  useEffect(() => {
    setqa(
      qas.map((qa) => {
        return (
          <div>
            <p className={classes.ques}>
              <p className={classes.author}>
                {qa.author !== null ? qa.author : "Anonymous"}
              </p>
              {qa.question}
            </p>
            <p className={classes.ans}>
              {qa.answer}
              <span className={classes.dislike} onClick={dislike}>
                <ion-icon name="thumbs-down"></ion-icon>
                {qa.downvotes}
              </span>
              <span className={classes.like} onClick={like}>
                {qa.upvotes}
                <ion-icon name="thumbs-up"></ion-icon>
              </span>
            </p>
          </div>
        );
      })
    );
  }, [qas]);
  return (
    <div>
      <h2 className={classes.qas}>My Answers</h2>
      <hr></hr>

      <p>
        {qa}
        {qa.length === 0 && <p>No answers yet.</p>}
      </p>
    </div>
  );
}

export default MyAnswers;
