import React, { useEffect, useState } from "react";
import classes from "./MyQuestions.module.css";

function MyQuestions() {
  //fetch answers and send as props
  const [qas, setqas] = useState([]);
  //fetch answers and send as props
  useEffect(() => {
    const token = localStorage.getItem("token");
    fetch("http://localhost:8080/allQuestions", {
      method: "GET",
      cache: "no-store",
      headers: {
        Authorization: "Bearer " + token,
      },
    })
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setqas(data.qalist);
      });
  }, []);
  const qa = qas.map((qa) => {
    return (
      <div>
        <p className={classes.ques}>{qa.question}</p>
        <p className={classes.ans}>
          {qa.answers.map((ans) => {
            console.log(ans);
            return (
              <p>
                {ans.map((a) => {
                  return (
                    <div>
                      <p className={classes.author}> {}</p>
                      <p>{}</p>
                      <hr />
                    </div>
                  );
                })}
              </p>
            );
          })}
        </p>
      </div>
    );
  });
  return (
    <div>
      <h2 className={classes.qas}>My Questions</h2>
      <p>{qa}</p>
    </div>
  );
}

export default MyQuestions;
